// To Do list
// Andrew Cashner
// 2024/02/23

import { useLoaderData } from "react-router-dom";
import { useState } from "react";
import ToDoContext from "../store/ToDoContext";
import ToDoList from "../classes/ToDoList";
import TaskList from "../components/TaskList";
import CheckAllButton from "../components/CheckAllButton";
import NewTaskForm from "../components/NewTaskForm";

function ToDo() {
  const loadedToDo = useLoaderData();

  let [items, setItems] = useState(new ToDoList());

  let toDoContextValue = {
    get: items,
    set: setItems,
    reset: () => setItems(new ToDoList())
  }

  return(
    <main>
      <ul>
        {loadedToDo.map(p => <li key={p.id}>{p.title}</li>)}
      </ul>
      <ToDoContext.Provider value={toDoContextValue}>
        <section id="todo">
          <div className="todoList">
            <h1>Tasks</h1>
            <p className="instructions">Add a new task using the form below. Drag to rearrange tasks.</p>
            <TaskList />
            <CheckAllButton />
          </div>
            <NewTaskForm />
        </section>
      </ToDoContext.Provider>
    </main>
  );
}

export default ToDo;

export async function loader() {
  let response = await fetch("https://jsonplaceholder.typicode.com/posts");
  if (!response.ok) {
    throw new Error("Could not access user data");
  }
  return response;
}
