// To Do list
// Andrew Cashner
// 2024/02/23

import "./App.css";
import { useLoaderData } from "react-router-dom";
import { useState } from "react";
import ToDoContext from "./store/ToDoContext";
import ToDoList from "./classes/ToDoList";
import TaskList from "./components/TaskList";
import CheckAllButton from "./components/CheckAllButton";
import NewTaskForm from "./components/NewTaskForm";

const emptyList = new ToDoList();

function App() {
  const loadedToDo = useLoaderData();

  let [items, setItems] = useState(loadedToDo);

  let toDoContextValue = {
    get: items,
    set: setItems,
    reset: () => setItems(emptyList)
  }

  return(
    <ToDoContext.Provider value={toDoContextValue}>
      <section id="todo">
        <div className="todoList">
          <h1>To Do</h1>
          <p className="instructions">Add a new task using the form below. Drag to rearrange tasks.</p>
          <TaskList />
          <CheckAllButton />
        </div>
          <NewTaskForm />
      </section>
    </ToDoContext.Provider>
  );
}

export default App;

export async function loader() {
  let response = "OK"; // await fetch(`localserver://username='${username}'&pw=${pw}`);
  if (!response.ok) {
    throw new Error("Could not access user data");
  }
//  return response;
  return emptyList;
}
